package com.xiablog.entity;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @version V1.0 * @author: 乐此不疲太难了 * @date: 2020/3/1 15:07 * @description: */@Configurationpublic class Student {    private String name;    private int age;    private String address;    public Student() {    }    public Student(String name, int age, String address) {        this.name = name;        this.age = age;        this.address = address;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    @Bean    public Student getStudent(){        Student student = new Student();        student.setName("xia");        student.setAddress("红安");        student.setAge(30);        return student;    }}